mapper.insert:
  	INSERT INTO ICT_COMMUNITY(	  CNUM
						  		, CSUBJECT
						  		, CCONTENT
						  		, CPHOTO
						  		, MNUM
						  		, DELETEYN
						  		, INSERTDATE
						  		, UPDATEDATE
						  	 )
					  VALUES(
		   				    #{cnum}
						  , #{csubject}
						  , #{ccontent}
						  , #{cphoto}
						  , #{mnum}
						  , 'Y'
						  , SYSDATE
						  , SYSDATE
						    )

mapper.update:
UPDATE "ORCLICT00"."ICT_COMMUNITY" A
   SET 	  CSUBJECT = #{csubject }
   		, CCONTENT = #{ccontent }
   		, CPHOTO = #{cphoto }
   		, UPDATEDATE = SYSDATE
 WHERE CNUM = #{cnum }
   AND DELETEYN = 'Y'
