mapper.update:
UPDATE "ORCLICT00"."ICT_TIP"
SET     TSUBJECT = #{tsubject}
       ,TCONTENT = #{tcontent}
       ,TPHOTO = #{tphoto}
       ,TCATEGORY = #{tcategory}
       ,UPDATEDATE = SYSDATE
WHERE   TNUM = #{tnum}
AND		DELETEYN = 'Y'

mapper.select:
SELECT A.MGRADE
FROM ICT_MEMBER A
WHERE A.MNUM = #{mnum}
